import { Component, OnInit } from '@angular/core';
import { BuyerService } from '../services/buyer.service'

@Component({
  selector: 'app-buyer-auth',
  templateUrl: './buyer-auth.component.html',
  styleUrls: ['./buyer-auth.component.css']
})

export class BuyerAuthComponent implements OnInit {
  constructor(private buyer: BuyerService) { }

  ngOnInit(): void { }

  userSignUp(data: object): void {
    console.warn(data);
    this.buyer.userSignUp(data).then(result => {
      console.warn("result", result);
    });
  }
}
<div class="buyer-auth">
  <div class="sign-up">
    <h1>Buyer sign-up</h1>
    <form #buyerSignUp="ngForm" (ngSubmit)="userSignUp(buyerSignUp.value)" class="common-form">
      <input
        type="text"
        class="form-input"
        placeholder="Enter UserName"
        name="UserName"
        ngModel
      />
      <input
        type="password"
        class="form-input"
        placeholder="Enter Password"
        name="password"
        ngModel
      />
      <input
        type="text"
        class="form-input"
        placeholder="Enter Email"
        name="email"
        ngModel
      />
      <button class="form-button">Sign Up</button>
    </form>
  </div>
</div>
