import { Injectable } from '@angular/core';
import * as sql from 'mssql';
import * as mssql from 'mssql';
// ...
const connection: mssql.ConnectionPool = new mssql.ConnectionPool(/* ... */);
connection.close(); // Assuming you're trying to call the 'close' method


@Injectable({
  providedIn: 'root'
})
export class DatabaseService {
  private config: sql.config = {
    server: '052123-batch-server.database.windows.net',
    database: '052123-batch',
    user: 'batch052123',
    password: 'Nubian19',
    options: {
      encrypt: true // Enable encryption for secure connection
    }
  };

  async executeQuery(query: string): Promise<any> {
    try {
      const pool = await new sql.ConnectionPool(this.config).connect();
      const result = await pool.request().query(query);
      return result.recordset;
    } catch (error) {
      console.log('Error:', error);
      throw error;
    } finally {
      sql.close();
    }
  }

  async getCustomers(): Promise<any> {
    const query = 'SELECT * FROM Customer';
    return await this.executeQuery(query);
  }

  async getInventory(): Promise<any> {
    const query = 'SELECT * FROM Inventory';
    return await this.executeQuery(query);
  }

  async getOrders(): Promise<any> {
    const query = 'SELECT * FROM [Order]';
    return await this.executeQuery(query);
  }

  async getProducts(): Promise<any> {
    const query = 'SELECT * FROM Products';
    return await this.executeQuery(query);
  }

  async getJunction(): Promise<any> {
    const query = 'SELECT * FROM StoreProductJunction';
    return await this.executeQuery(query);
  }

  async getStores(): Promise<any> {
    const query = 'SELECT * FROM Stores';
    return await this.executeQuery(query);
  }
}
